<script>
    $(document).ready(function () {
      // add/remove an Ed Standards button tag from the checkboxes lists
      $('input.form-checkbox').change('click', function () {
          var checkboxValue = $(this).val();
          var checkboxText = $(this).data('text');
          var checkboxBtn = '<span class="course-tag-item" filter="erc_filetype_id" id="standard-' + checkboxValue + '" value="' + checkboxValue + '">' + checkboxText + '<button class="close-tag" type="button">X</button></span>';
          if (this.checked) {
              $('.erc.course-tag-container').append(checkboxBtn);
          } else {
              $('.erc.course-tag-container span.course-tag-item').remove('#standard-' + checkboxValue);
          }
          // remove the Ed Standard Button/tags and unchecks item
          $('span#standard-' + checkboxValue).click(function () {
            var selection = $('input[type=checkbox][value="' + checkboxValue + '"]');
              $(this).remove();
              selection.prop('checked', false);
          });
      });
      // toggle/expanded collapse submenus
      $('#education-standard-filters li').click(function () {
          if ($(this).hasClass("selected")) {
              $(this).removeClass("selected")
          } else {
              $(this).addClass("selected")
          }
      });
    });
</script>

